import React, { Component } from "react";
import "./congressPersonDisplay.css";
import { getButtons } from "./fetch_data_functions";
import PlaceHolder from "./rep_placeholder_image.jpg";

class CongressPersonDisplay extends Component {
  constructor() {
    super();
    this.state = {
      repBio: null,
      repInfo: [],
      billsSponsored: null,
      billsPassed: null,
      partisanDegree: null,
      bipartisanDegree: null,
      active: false,
      type: "congresspersondisplay"
    };
  }

  componentWillMount = () => {
    this.getAll();
  };

  getDropdown = () => {
    const currentState = this.state.active;
    this.setState({ active: !currentState });
  };

  filterFunc = () => {
    var input, filter, a, i, div;
    input = document.getElementById("myINput");
    filter = input.value.toUpperCase();
    div = document.getElementById("myDropDown");
    a = div.getElementsByTagName("div");
    for (i = 0; i < a.length; i++) {
      var txtValue = a[i].textContent || a[i].innerText;
      if (txtValue.toUpperCase().indexOf(filter) > -1) {
        a[i].style.display = "";
      } else {
        a[i].style.display = "none";
      }
    }
  };

  getAll = () => {
    getButtons(116).then(data => {
      this.setState({
        repInfo: [...data]
      });
    });
  };

  componentWillReceiveProps = props => {
    getButtons(props.congress).then(data => {
      this.setState({
        repInfo: [...data]
      });
    });
    this.setState({ repBio: props.currentMember });
  };

  render() {
    var rep_Name;
    if (this.state.repBio == null) {
      rep_Name = <h6 className="name">Choose Your Senator</h6>;
    } else {
      rep_Name = <h6 className="name">{this.state.repBio[0]}</h6>;
    }

    var message;
    var currImg;
    if (this.state.repBio == null) {
      message = (
        <div className="onMountMessage">
          Click on a Senator To Get More Information About That Congress Member
        </div>
      );
      currImg = (
        <img className="smallPhoto" src={PlaceHolder} alt="Placeholder" />
      );
    } else {
      message = (
        <div className="properties">
          <div className="bills">
            <div className="sponsored">
              <h6>Bills Sponsored</h6>
              <h3>0</h3>
            </div>
            <div className="passed">
              <h6>Bills Passed</h6>
              <h3>0</h3>
            </div>
          </div>
          <div className="partisan">
            <div className="part">
              <h6>Partisan Degree</h6>
              <h3>0</h3>
            </div>
            <div className="bipart">
              <h6>Bipartisan Degree</h6>
              <h3>0</h3>
            </div>
          </div>
        </div>
      );
      currImg = (
        <img
          className="smallPhoto"
          src={
            "http://bioguide.congress.gov/bioguide/photo/" +
            this.state.repBio[1].substring(0, 1) +
            "/" +
            this.state.repBio[1] +
            ".jpg"
          }
          alt="Placeholder"
        />
      );
    }

    return (
      <div className="wrapper">
        {rep_Name}
        {currImg}
        <div className="textContainer">
          <div className="nameAndSearch">
            <div className="dropDown">
              <input
                type="text"
                placeholder="Search Rep..."
                id="myINput"
                onKeyUp={this.filterFunc}
                onClick={this.getDropdown}
              />
              <div
                id="myDropDown"
                className={
                  this.state.active
                    ? "dropDown-Content Show"
                    : "dropDown-Content"
                }
              >
                {Object.keys(this.state.repInfo).map(idx => {
                  var name = this.state.repInfo[idx][0];
                  var bioGuide = this.state.repInfo[idx][1];
                  return (
                    <div
                      onClick={() => {
                        this.setState({
                          repBio: [name.first + " " + name.last, bioGuide],
                          active: false
                        });
                      }}
                      key={idx}
                    >
                      {name.first + " " + name.last}
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
          {message}
        </div>
      </div>
    );
  }
}

export default CongressPersonDisplay;
